import React, { useState, useEffect } from 'react';
import { Document, Page, pdfjs } from 'react-pdf';
import { Button, Modal, Typography, Slider, AppBar, Toolbar, IconButton, TextField, Box, Container } from '@mui/material';
import { PlayArrow, Stop, Close, UploadFile, Sync } from '@mui/icons-material';
import './App.css';

pdfjs.GlobalWorkerOptions.workerSrc = `//unpkg.com/pdfjs-dist@${pdfjs.version}/build/pdf.worker.min.mjs`;

function App() {
  const [file, setFile] = useState(null);
  const [textArray, setTextArray] = useState([]);
  const [isTextLoaded, setIsTextLoaded] = useState(false);
  const [speed, setSpeed] = useState(100);
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [isReading, setIsReading] = useState(false);
  const [pageNumber, setPageNumber] = useState(1);
  const [numPages, setNumPages] = useState(null);
  const [bookTitle, setBookTitle] = useState('');
  const [showModal, setShowModal] = useState(false);

  function onFileChange(event) {
    const file = event.target.files[0];
    if (file) {
      setFile(file);
      setPageNumber(1);
      setIsTextLoaded(false);
      setBookTitle(file.name);
    }
  }

  function onDocumentLoadSuccess(pdf) {
    setNumPages(pdf.numPages);
  }

  async function extractTextFromSelectedPage(pdfFile, pageNum) {
    try {
      const loadingTask = pdfjs.getDocument({ url: URL.createObjectURL(pdfFile) });
      const pdf = await loadingTask.promise;
      const page = await pdf.getPage(pageNum);
      const textContent = await page.getTextContent();
      const textItems = textContent.items.map((item) => item.str);
      const text = textItems.join(' ');
      const wordsArray = text.split(' ').filter((word) => word.trim() !== '');
      setTextArray(wordsArray);
      setCurrentWordIndex(0);
      setIsTextLoaded(true);
      setShowModal(true);
    } catch (error) {
      console.error("Error extracting text from PDF: ", error);
    }
  }

  function startReading() {
    setIsReading(true);
  }

  function stopReading() {
    setIsReading(false);
  }

  useEffect(() => {
    let interval = null;
    if (isReading) {
      interval = setInterval(() => {
        setCurrentWordIndex((prevIndex) => {
          if (prevIndex < textArray.length - 1) {
            return prevIndex + 1;
          } else {
            clearInterval(interval);
            setIsReading(false);
            return prevIndex;
          }
        });
      }, (60 / speed) * 1000);
    } else {
      clearInterval(interval);
    }
    return () => clearInterval(interval);
  }, [isReading, speed, textArray]);

  return (
    <Container>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            {bookTitle || "Choose a book"}
          </Typography>
        </Toolbar>
      </AppBar>

      <Box my={2}>
        {file && (
          <Box display="flex" justifyContent="center">
            <Document file={file} onLoadSuccess={onDocumentLoadSuccess}>
              <Page pageNumber={pageNumber} />
            </Document>
          </Box>
        )}
      </Box>

      <Box display="flex" justifyContent="center" my={2}>
        {!file && (
          <Button variant="contained" component="label" startIcon={<UploadFile />}>
            Upload PDF
            <input type="file" accept="application/pdf" onChange={onFileChange} hidden />
          </Button>
        )}
        {file && (
          <>
            <TextField
              type="number"
              label="Page Number"
              variant="outlined"
              value={pageNumber}
              onChange={(e) => setPageNumber(parseInt(e.target.value) || 1)}
              inputProps={{ min: 1, max: numPages }}
            />
            <IconButton color="primary" onClick={() => extractTextFromSelectedPage(file, pageNumber)}>
              <Sync />
            </IconButton>
          </>
        )}
      </Box>

      <Modal open={showModal} onClose={() => setShowModal(false)}>
        <Box sx={{ bgcolor: 'background.paper', p: 4, borderRadius: 1 }}>
          <Typography variant="h5" gutterBottom>
            {bookTitle}
          </Typography>
          {isTextLoaded && (
            <>
              {!isReading ? (
                <>
                  <Button variant="contained" color="primary" onClick={startReading} startIcon={<PlayArrow />}>
                    Start
                  </Button>
                  <Button variant="outlined" color="secondary" onClick={() => setShowModal(false)} startIcon={<Close />}>
                    Close
                  </Button>
                </>
              ) : (
                <>
                  <Typography variant="h4" align="center" gutterBottom>
                    {textArray[currentWordIndex]}
                  </Typography>
                  <Box my={2}>
                    <Typography gutterBottom>Speed: {speed} WPM</Typography>
                    <Slider
                      value={speed}
                      onChange={(e, newValue) => setSpeed(newValue)}
                      min={50}
                      max={400}
                      valueLabelDisplay="auto"
                    />
                  </Box>
                  <Button variant="contained" color="error" onClick={stopReading} startIcon={<Stop />}>
                    Stop
                  </Button>
                </>
              )}
            </>
          )}
        </Box>
      </Modal>
    </Container>
  );
}

export default App;
