import React, { useState, useEffect } from 'react';
import { Document, Page, pdfjs } from 'react-pdf';
import { BottomNavigation, BottomNavigationAction } from '@mui/material';
import DescriptionIcon from '@mui/icons-material/Description'; // Иконка для выбора файла
import PlayArrowIcon from '@mui/icons-material/PlayArrow'; // Иконка старт
import StopIcon from '@mui/icons-material/Stop'; // Иконка стоп
import PageviewIcon from '@mui/icons-material/Pageview'; // Иконка выбора страницы
import './App.css';

// Указываем путь к воркеру для PDF.js
pdfjs.GlobalWorkerOptions.workerSrc = `//unpkg.com/pdfjs-dist@${pdfjs.version}/build/pdf.worker.min.mjs`;

function App() {
  const [file, setFile] = useState(null);
  const [textArray, setTextArray] = useState([]);
  const [isTextLoaded, setIsTextLoaded] = useState(false);
  const [speed, setSpeed] = useState(100);
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [isReading, setIsReading] = useState(false);
  const [pageNumber, setPageNumber] = useState(1);
  const [numPages, setNumPages] = useState(null);
  const [selectedPage, setSelectedPage] = useState(null);
  const [bookTitle, setBookTitle] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [value, setValue] = useState(0);

  function onFileChange(event) {
    const file = event.target.files[0];
    if (file) {
      setFile(file);
      setPageNumber(1);
      setSelectedPage(null);
      setIsTextLoaded(false);
      setBookTitle(file.name);
    }
  }

  function onDocumentLoadSuccess(pdf) {
    setNumPages(pdf.numPages);
  }

  async function extractTextFromSelectedPage(pdfFile, pageNum) {
    // Тот же код для извлечения текста
  }

  function startReading() {
    setIsReading(true);
  }

  function stopReading() {
    setIsReading(false);
  }

  useEffect(() => {
    // Тот же useEffect для обновления слов
  }, [isReading, speed, textArray]);

  return (
    <div className="App">
      <header className="App-header">
        <h1>{bookTitle || "Choose a book"}</h1>
      </header>

      <main className="App-main">
        {!file && (
          <label className="file-label ios-button">
            Select PDF File
            <input type="file" accept="application/pdf" onChange={onFileChange} className="file-input" />
          </label>
        )}
        {file && (
          <>
            <div className="pdf-container">
              <Document
                file={file}
                onLoadSuccess={onDocumentLoadSuccess}
              >
                <Page pageNumber={pageNumber} />
              </Document>
            </div>
          </>
        )}
      </main>

      <BottomNavigation
        showLabels
        value={value}
        onChange={(event, newValue) => setValue(newValue)}
        className="bottom-nav"
      >
        <BottomNavigationAction label="Select File" icon={<DescriptionIcon />} onClick={() => document.querySelector('.file-input').click()} />
        <BottomNavigationAction label="Start Reading" icon={<PlayArrowIcon />} onClick={startReading} />
        <BottomNavigationAction label="Stop Reading" icon={<StopIcon />} onClick={stopReading} />
        <BottomNavigationAction label="Select Page" icon={<PageviewIcon />} onClick={() => setSelectedPage(pageNumber)} />
      </BottomNavigation>

      {showModal && (
        <div className="modal">
          {/* Ваш Modal */}
        </div>
      )}
    </div>
  );
}

export default App;
