import React, { useState, useEffect } from 'react';
import pdfToText from 'react-pdftotext';
import './App.css';

function App() {
  const [file, setFile] = useState(null); // Для хранения загруженного PDF
  const [textArray, setTextArray] = useState([]); // Для хранения массива слов
  const [isTextLoaded, setIsTextLoaded] = useState(false); // Флаг для отображения кнопки "Старт"
  const [speed, setSpeed] = useState(100); // Скорость (слов в минуту)
  const [currentWordIndex, setCurrentWordIndex] = useState(0); // Индекс текущего слова
  const [isReading, setIsReading] = useState(false); // Флаг чтения
  const [intervalId, setIntervalId] = useState(null); // Для хранения таймера

  // Функция для загрузки PDF файла
  function onFileChange(event) {
    const file = event.target.files[0];
    if (file) {
      setFile(file); // Сохраняем выбранный файл
      extractTextFromPDF(file); // Извлекаем текст из всего документа
    }
  }

  // Извлечение текста из PDF с помощью react-pdftotext
  function extractTextFromPDF(file) {
    const reader = new FileReader();
    reader.onload = function (e) {
      const arrayBuffer = e.target.result;
      const blob = new Blob([new Uint8Array(arrayBuffer)], { type: 'application/pdf' });

      pdfToText(blob) // Извлекаем текст из всего документа
        .then((text) => {
          const wordsArray = text
            .replace(/\s+/g, ' ') // Убираем лишние пробелы
            .split(' '); // Разбиваем текст на массив слов
          setTextArray(wordsArray); // Сохраняем слова в массив
          setIsTextLoaded(true); // Отображаем кнопку "Старт"
        })
        .catch((error) => {
          console.error("Error extracting text from PDF: ", error);
        });
    };
    reader.readAsArrayBuffer(file);
  }

  // Запуск процесса показа слов
  function startReading() {
    setIsReading(true);
    const interval = setInterval(() => {
      setCurrentWordIndex((prevIndex) => {
        if (prevIndex < textArray.length - 1) {
          return prevIndex + 1;
        } else {
          clearInterval(interval);
          return prevIndex;
        }
      });
    }, (60 / speed) * 1000); // Устанавливаем интервал на основе скорости слов в минуту
    setIntervalId(interval);
  }

  // Остановка показа слов
  function stopReading() {
    setIsReading(false);
    clearInterval(intervalId);
  }

  // Очищаем таймер при остановке
  useEffect(() => {
    return () => clearInterval(intervalId);
  }, [intervalId]);

  return (
    <div className="App">
      <header className="App-header">
        <input type="file" accept="application/pdf" onChange={onFileChange} />
        {isTextLoaded && (
          <>
            <div>
              <input
                type="range"
                min="10"
                max="300"
                value={speed}
                onChange={(e) => setSpeed(e.target.value)}
              />
              <label>Words per minute: {speed}</label>
            </div>
            {!isReading ? (
              <button onClick={startReading}>Start</button>
            ) : (
              <button onClick={stopReading}>Stop</button>
            )}
          </>
        )}
        {isReading && <h1>{textArray[currentWordIndex]}</h1>}
      </header>
    </div>
  );
}

export default App;
