import './App.css';
import { Document, Page } from 'react-pdf';
import { useState } from 'react';
import './App.css';


function App() {
  const [numPages, setNumPages] = useState(null); // Количество страниц в документе
  const [pageNumber, setPageNumber] = useState(1); // Номер текущей страницы

  // Функция, которая срабатывает при загрузке документа
  function onDocumentLoadSuccess({ numPages }) {
    setNumPages(numPages);
  }

  return (
    <div className="App">
      <header className="App-header">
        <Document
          file="path_to_pdf.pdf" // Укажи путь к PDF файлу
          onLoadSuccess={onDocumentLoadSuccess}
        >
          <Page pageNumber={pageNumber} />
        </Document>
        <p>
          Page {pageNumber} of {numPages}
        </p>

        {/* Кнопки для переключения страниц */}
        <button
          disabled={pageNumber <= 1}
          onClick={() => setPageNumber(pageNumber - 1)}
        >
          Previous
        </button>
        <button
          disabled={pageNumber >= numPages}
          onClick={() => setPageNumber(pageNumber + 1)}
        >
          Next
        </button>
      </header>
    </div>
  );
}

export default App;
