import React, { useState, useEffect } from 'react';
import { Document, Page, pdfjs } from 'react-pdf';
import { App as KonstaApp, Page as KonstaPage, Navbar, Block, Button, Tabbar, TabbarLink } from 'konsta/react';

pdfjs.GlobalWorkerOptions.workerSrc = `//unpkg.com/pdfjs-dist@${pdfjs.version}/build/pdf.worker.min.mjs`;

function App() {
  const [file, setFile] = useState(null);
  const [textArray, setTextArray] = useState([]);
  const [isTextLoaded, setIsTextLoaded] = useState(false);
  const [speed, setSpeed] = useState(100);
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [isReading, setIsReading] = useState(false);
  const [pageNumber, setPageNumber] = useState(1);
  const [numPages, setNumPages] = useState(null);
  const [bookTitle, setBookTitle] = useState('');
  const [showModal, setShowModal] = useState(false);

  function onFileChange(event) {
    const file = event.target.files[0];
    if (file) {
      setFile(file);
      setPageNumber(1);
      setIsTextLoaded(false);
      setBookTitle(file.name);
    }
  }

  function onDocumentLoadSuccess(pdf) {
    setNumPages(pdf.numPages);
  }

  async function extractTextFromSelectedPage(pdfFile, pageNum) {
    // Text extraction logic...
  }

  function startReading() {
    setIsReading(true);
  }

  function stopReading() {
    setIsReading(false);
  }

  useEffect(() => {
    let interval = null;
    if (isReading) {
      interval = setInterval(() => {
        setCurrentWordIndex((prevIndex) => {
          if (prevIndex < textArray.length - 1) {
            return prevIndex + 1;
          } else {
            clearInterval(interval);
            setIsReading(false);
            return prevIndex;
          }
        });
      }, (60 / speed) * 1000);
    } else {
      clearInterval(interval);
    }
    return () => clearInterval(interval);
  }, [isReading, speed, textArray]);

  return (
    <KonstaApp theme="ios">
      <KonstaPage>
        <Navbar title={bookTitle || "Choose a book"} />

        <Block strong>
          <main>
            {file && (
              <div className="pdf-container">
                <Document file={file} onLoadSuccess={onDocumentLoadSuccess}>
                  <Page pageNumber={pageNumber} />
                </Document>
              </div>
            )}
          </main>
        </Block>

        <Tabbar labels bottom>
          <TabbarLink label="Open File">
            <label>
              <input type="file" accept="application/pdf" onChange={onFileChange} className="file-input" />
              <Button className="tab-bar-item">Upload</Button>
            </label>
          </TabbarLink>

          {file && (
            <>
              <TabbarLink label="Previous Page">
                <Button onClick={() => setPageNumber(pageNumber > 1 ? pageNumber - 1 : 1)}>-</Button>
              </TabbarLink>

              <TabbarLink label="Next Page">
                <Button onClick={() => setPageNumber(pageNumber < numPages ? pageNumber + 1 : numPages)}>+</Button>
              </TabbarLink>

              <TabbarLink label="Extract Text">
                <Button onClick={() => extractTextFromSelectedPage(file, pageNumber)}>Extract</Button>
              </TabbarLink>
            </>
          )}
        </Tabbar>
      </KonstaPage>

      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>{bookTitle}</h2>
            {!isReading ? (
              <>
                <Button onClick={startReading} iconIos="play">Start</Button>
                <Button onClick={() => setShowModal(false)}>Close</Button>
              </>
            ) : (
              <>
                <h1>{textArray[currentWordIndex]}</h1>
                <div className="range-container">
                  <input type="range" value={speed} onChange={(e) => setSpeed(e.target.value)} />
                </div>
                <Button onClick={stopReading} iconIos="stop">Stop</Button>
              </>
            )}
          </div>
        </div>
      )}
    </KonstaApp>
  );
}

export default App;
