import React, { useState } from 'react';
import { Document, Page, pdfjs } from 'react-pdf';
import pdfToText from 'react-pdftotext';
import './App.css';

// Указываем путь к воркеру pdfjs
pdfjs.GlobalWorkerOptions.workerSrc = new URL(
  'pdfjs-dist/build/pdf.worker.min.mjs',
  import.meta.url,
).toString();

function App() {
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);
  const [file, setFile] = useState(null); // Для хранения загруженного PDF

  // Функция обработки загрузки документа
  function onDocumentLoadSuccess({ numPages }) {
    setNumPages(numPages); // Устанавливаем количество страниц из pdf объекта
  }

  // Функция для загрузки PDF файла
  function onFileChange(event) {
    const file = event.target.files[0];
    if (file) {
      setFile(URL.createObjectURL(file)); // Создаем URL для загруженного файла

      // Извлечение текста из загруженного PDF с использованием react-pdftotext
      extractText(file);
    }
  }

  // Извлечение текста с помощью react-pdftotext
  function extractText(file) {
    pdfToText(file)
      .then((text) => {
        console.log("Extracted text from PDF: ", text);
      })
      .catch((error) => {
        console.error("Error extracting text: ", error);
      });
  }

  return (
    <div className="App">
      <header className="App-header">
        <input type="file" accept="application/pdf" onChange={onFileChange} />
        {file && (
          <>
            {/* Отображение PDF с помощью react-pdf */}
            <Document
              file={file}
              onLoadSuccess={onDocumentLoadSuccess}
            >
              <Page pageNumber={pageNumber} />
            </Document>
            <p>
              Page {pageNumber} of {numPages}
            </p>
            <button
              disabled={pageNumber <= 1}
              onClick={() => setPageNumber(pageNumber - 1)}
            >
              Previous
            </button>
            <button
              disabled={pageNumber >= numPages}
              onClick={() => setPageNumber(pageNumber + 1)}
            >
              Next
            </button>
          </>
        )}
      </header>
    </div>
  );
}

export default App;
