import React, { useState } from 'react';
import { Document, Page, pdfjs } from 'react-pdf';
import './App.css';

// Указываем путь к worker файлу для pdfjs
pdfjs.GlobalWorkerOptions.workerSrc = new URL(
  'pdfjs-dist/build/pdf.worker.min.mjs',
  import.meta.url,
).toString();

function App() {
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);
  const [bookName, setBookName] = useState("");  // Название книги
  const [pdfFile, setPdfFile] = useState(null);  // Загруженный PDF файл

  // Срабатывает при успешной загрузке PDF
  function onDocumentLoadSuccess({ numPages }) {
    setNumPages(numPages);
    setPageNumber(1);  // Сбрасываем страницу на первую при новой загрузке
  }

  // Обработчик для импорта книги (загрузка файла)
  function handleFileUpload(event) {
    const file = event.target.files[0];
    if (file) {
      setPdfFile(file);           // Устанавливаем загруженный PDF файл
      setBookName(file.name);     // Устанавливаем название книги
    }
  }

  return (
    <div className="App">
      <header className="App-header">
        {/* Название книги */}
        <h1>{bookName ? bookName : "Выберите книгу"}</h1>

        {/* Поле для загрузки PDF файла */}
        <input type="file" accept="application/pdf" onChange={handleFileUpload} />

        {/* Отображение PDF-документа, если файл загружен */}
        {pdfFile && (
          <>
            <Document
              file={pdfFile}
              onLoadSuccess={onDocumentLoadSuccess}
            >
              <Page pageNumber={pageNumber} />
            </Document>

            {/* Информация о текущей странице и количестве страниц */}
            <p>
              Страница {pageNumber} из {numPages}
            </p>

            {/* Кнопки для перехода между страницами */}
            <div>
              <button
                disabled={pageNumber <= 1}
                onClick={() => setPageNumber(pageNumber - 1)}
              >
                Назад
              </button>
              <button
                disabled={pageNumber >= numPages}
                onClick={() => setPageNumber(pageNumber + 1)}
              >
                Вперед
              </button>
            </div>
          </>
        )}
      </header>
    </div>
  );
}

export default App;
