import React, { useState, useEffect } from 'react';
import { Document, Page, pdfjs } from 'react-pdf';
import './App.css';
import { FaHome, FaFilePdf, FaCog, FaBook } from 'react-icons/fa'; // Добавляем иконки

pdfjs.GlobalWorkerOptions.workerSrc = `//unpkg.com/pdfjs-dist@${pdfjs.version}/build/pdf.worker.min.mjs`;

function App() {
  const [file, setFile] = useState(null);
  const [textArray, setTextArray] = useState([]);
  const [isTextLoaded, setIsTextLoaded] = useState(false);
  const [speed, setSpeed] = useState(100);
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [isReading, setIsReading] = useState(false);
  const [pageNumber, setPageNumber] = useState(1);
  const [numPages, setNumPages] = useState(null);
  const [selectedPage, setSelectedPage] = useState(null);
  const [bookTitle, setBookTitle] = useState('');
  const [showModal, setShowModal] = useState(false);

  function onFileChange(event) {
    const file = event.target.files[0];
    if (file) {
      setFile(file);
      setPageNumber(1);
      setSelectedPage(null);
      setIsTextLoaded(false);
      setBookTitle(file.name);
    }
  }

  function onDocumentLoadSuccess(pdf) {
    setNumPages(pdf.numPages);
  }

  async function extractTextFromSelectedPage(pdfFile, pageNum) {
    try {
      const loadingTask = pdfjs.getDocument({ url: URL.createObjectURL(pdfFile) });
      const pdf = await loadingTask.promise;
      const page = await pdf.getPage(pageNum);
      const textContent = await page.getTextContent();
      const textItems = textContent.items.map((item) => item.str);
      const text = textItems.join(' ');
      const wordsArray = text.split(' ').filter((word) => word.trim() !== '');
      setTextArray(wordsArray);
      setCurrentWordIndex(0);
      setIsTextLoaded(true);
      setShowModal(true);
    } catch (error) {
      console.error("Error extracting text from PDF: ", error);
    }
  }

  useEffect(() => {
    let interval = null;
    if (isReading) {
      interval = setInterval(() => {
        setCurrentWordIndex((prevIndex) => {
          if (prevIndex < textArray.length - 1) {
            return prevIndex + 1;
          } else {
            clearInterval(interval);
            setIsReading(false);
            return prevIndex;
          }
        });
      }, (60 / speed) * 1000);
    } else {
      clearInterval(interval);
    }
    return () => clearInterval(interval);
  }, [isReading, speed, textArray]);

  return (
    <div className="App">
      <header className="App-header">
        <h1>{bookTitle || "Choose a book"}</h1>
      </header>

      <main className="App-main">
        {file && (
          <div className="pdf-container">
            <Document file={file} onLoadSuccess={onDocumentLoadSuccess}>
              <Page pageNumber={pageNumber} />
            </Document>
          </div>
        )}
      </main>

      {/* Tab bar with icons */}
      <footer className="App-footer">
        <div className="tab-bar">
          <div className="tab-item">
            <FaHome size={24} />
            <span>Home</span>
          </div>
          <div className="tab-item">
            <FaFilePdf size={24} />
            <span>PDF</span>
          </div>
          <div className="tab-item">
            <FaBook size={24} />
            <span>Books</span>
          </div>
          <div className="tab-item">
            <FaCog size={24} />
            <span>Settings</span>
          </div>
        </div>
      </footer>

      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>{bookTitle}</h2>
            {isTextLoaded && (
              <>
                {!isReading ? (
                  <>
                    <button onClick={() => setIsReading(true)} className="ios-button">
                      Start
                    </button>
                    <button onClick={() => setShowModal(false)} className="ios-button close-button">
                      Close
                    </button>
                  </>
                ) : (
                  <>
                    <h1 className="word-display">{textArray[currentWordIndex]}</h1>
                    <div className="range-container">
                      <span className="range-value">{speed} WPM</span>
                      <input
                        type="range"
                        min="50"
                        max="400"
                        value={speed}
                        onChange={(e) => setSpeed(e.target.value)}
                        className="speed-range"
                      />
                    </div>
                    <button onClick={() => setIsReading(false)} className="ios-button">
                      Stop
                    </button>
                  </>
                )}
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
