import React, { useState, useEffect } from 'react';
import { Document, Page, pdfjs } from 'react-pdf';
import './App.css';

// Указываем путь к воркеру для PDF.js
pdfjs.GlobalWorkerOptions.workerSrc = `//unpkg.com/pdfjs-dist@${pdfjs.version}/build/pdf.worker.min.mjs`;

function App() {
  const [file, setFile] = useState(null);
  const [textArray, setTextArray] = useState([]);
  const [isTextLoaded, setIsTextLoaded] = useState(false);
  const [speed, setSpeed] = useState(100);
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [isReading, setIsReading] = useState(false);
  const [pageNumber, setPageNumber] = useState(1);
  const [numPages, setNumPages] = useState(null);
  const [selectedPage, setSelectedPage] = useState(null);
  const [bookTitle, setBookTitle] = useState('');
  const [showModal, setShowModal] = useState(false);

  // Функция для загрузки PDF файла
  function onFileChange(event) {
    const file = event.target.files[0];
    if (file) {
      setFile(file);
      setPageNumber(1);
      setSelectedPage(null);
      setIsTextLoaded(false);
      setBookTitle(file.name);
    }
  }

  // Функция обработки загрузки документа PDF
  function onDocumentLoadSuccess(pdf) {
    setNumPages(pdf.numPages);
  }

  // Извлечение текста с выбранной страницы с использованием pdfjs
  async function extractTextFromSelectedPage(pdfFile, pageNum) {
    try {
      const loadingTask = pdfjs.getDocument({ url: URL.createObjectURL(pdfFile) });
      const pdf = await loadingTask.promise;
      const page = await pdf.getPage(pageNum);
      const textContent = await page.getTextContent();
      const textItems = textContent.items.map((item) => item.str);
      const text = textItems.join(' ');
      const wordsArray = text.split(' ').filter((word) => word.trim() !== '');
      setTextArray(wordsArray);
      setCurrentWordIndex(0);
      setIsTextLoaded(true);
      setShowModal(true);
    } catch (error) {
      console.error("Error extracting text from PDF: ", error);
    }
  }

  // Запуск процесса показа слов
  function startReading() {
    setIsReading(true);
  }

  // Остановка показа слов
  function stopReading() {
    setIsReading(false);
  }

  // Закрытие модалки
  function closeModal() {
    setShowModal(false);
    setIsReading(false);
  }

  // Обновление индекса текущего слова
  useEffect(() => {
    let interval = null;
    if (isReading) {
      interval = setInterval(() => {
        setCurrentWordIndex((prevIndex) => {
          if (prevIndex < textArray.length - 1) {
            return prevIndex + 1;
          } else {
            clearInterval(interval);
            setIsReading(false);
            return prevIndex;
          }
        });
      }, (60 / speed) * 1000);
    } else {
      clearInterval(interval);
    }
    return () => clearInterval(interval);
  }, [isReading, speed, textArray]);

  return (
    <div className="App">
      {/* Header */}
      <header className="App-header">
        <h1>{bookTitle || "Choose a book"}</h1>
      </header>

      {/* Main content for PDF display */}
      <main className="App-main">
        {!file && (
          <label className="file-label ios-button">
            Select PDF File
            <input type="file" accept="application/pdf" onChange={onFileChange} className="file-input" />
          </label>
        )}
        {file && (
          <>
            <div className="pdf-container">
              <Document
                file={file}
                onLoadSuccess={onDocumentLoadSuccess}
              >
                <Page pageNumber={pageNumber} />
              </Document>
            </div>
            <div className="controls">
              <label>Go to page: </label>
              <input
                type="number"
                min="1"
                max={numPages}
                value={pageNumber}
                onChange={(e) => setPageNumber(parseInt(e.target.value) || 1)}
                className="page-input"
              />
              <button onClick={() => setSelectedPage(pageNumber)} className="ios-button">
                Select Page
              </button>
            </div>
          </>
        )}
      </main>

      {/* Footer */}
      <footer className="App-footer">
        {file && (
          <label className="file-label-small ios-button">
            Select another file
            <input type="file" accept="application/pdf" onChange={onFileChange} className="file-input" />
          </label>
        )}
      </footer>

      {/* Modal */}
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <button className="close-button" onClick={closeModal}>X</button>
            <h2>{bookTitle}</h2>

            {/* Эквалайзер */}
            <svg className="equalizer" viewBox="0 0 100 20">
              <rect className="bar" x="10" y="5" width="5" height={Math.max(2, 10 - speed / 20)} />
              <rect className="bar" x="20" y="5" width="5" height={Math.max(2, 10 - speed / 30)} />
              <rect className="bar" x="30" y="5" width="5" height={Math.max(2, 10 - speed / 40)} />
              <rect className="bar" x="40" y="5" width="5" height={Math.max(2, 10 - speed / 50)} />
              <rect className="bar" x="50" y="5" width="5" height={Math.max(2, 10 - speed / 60)} />
            </svg>

            {isTextLoaded && (
              <>
                {!isReading ? (
                  <>
                    <button onClick={startReading} className="ios-button">
                      Start
                    </button>
                    <button onClick={() => setShowModal(false)} className="ios-button close-button">
                      Close
                    </button>
                  </>
                ) : (
                  <>
                    {textArray[currentWordIndex] && (
                      <h1 className="word-display">{textArray[currentWordIndex]}</h1>
                    )}
                    <div className="range-container">
                      <label htmlFor="speed-range">Speed: {speed} WPM</label>
                      <input
                        type="range"
                        id="speed-range"
                        min="50"
                        max="400"
                        value={speed}
                        onChange={(e) => setSpeed(e.target.value)}
                        className="speed-range"
                      />
                    </div>
                    <button onClick={stopReading} className="ios-button">
                      Stop
                    </button>
                  </>
                )}
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
