import React, { useState } from 'react';
import { Document, Page, pdfjs } from 'react-pdf';
import pdfToText from 'react-pdftotext';
import './App.css';

// Указываем путь к воркеру pdfjs
pdfjs.GlobalWorkerOptions.workerSrc = new URL(
  'pdfjs-dist/build/pdf.worker.min.mjs',
  import.meta.url,
).toString();

function App() {
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);
  const [file, setFile] = useState(null); // Для хранения загруженного PDF

  // Функция обработки загрузки документа
  function onDocumentLoadSuccess({ numPages }) {
    setNumPages(numPages); // Устанавливаем количество страниц из pdf объекта
  }

  // Функция для загрузки PDF файла
  function onFileChange(event) {
    const file = event.target.files[0];
    if (file) {
      setFile(URL.createObjectURL(file)); // Создаем URL для загруженного файла

      // Извлечение текста с первой страницы загруженного PDF
      extractTextFromPage(file, 1); // Извлекаем текст с 1 страницы
    }
  }

  // Извлечение текста с определенной страницы PDF с помощью react-pdftotext
  function extractTextFromPage(file, pageNumber) {
    const reader = new FileReader();
    reader.onload = function (e) {
      const arrayBuffer = e.target.result;
      const blob = new Blob([new Uint8Array(arrayBuffer)], { type: 'application/pdf' });

      pdfToText(blob, { pages: [pageNumber] }) // Указываем, что нужна только первая страница
        .then((text) => {
          console.log(`Extracted text from page ${pageNumber}: `, text);
        })
        .catch((error) => {
          console.error(`Error extracting text from page ${pageNumber}: `, error);
        });
    };
    reader.readAsArrayBuffer(file);
  }

  return (
    <div className="App">
      <header className="App-header">
        <input type="file" accept="application/pdf" onChange={onFileChange} />
        {file && (
          <>
            {/* Отображение всех страниц PDF с помощью react-pdf */}
            <Document
              file={file}
              onLoadSuccess={onDocumentLoadSuccess}
            >
              {Array.from(new Array(numPages), (el, index) => (
                <Page key={`page_${index + 1}`} pageNumber={index + 1} />
              ))}
            </Document>
            <p>
              Page {pageNumber} of {numPages}
            </p>
            <button
              disabled={pageNumber <= 1}
              onClick={() => setPageNumber(pageNumber - 1)}
            >
              Previous
            </button>
            <button
              disabled={pageNumber >= numPages}
              onClick={() => setPageNumber(pageNumber + 1)}
            >
              Next
            </button>
          </>
        )}
      </header>
    </div>
  );
}

export default App;
